const videoAi=document.getElementById("videoAi");const videoAnalysis=document.getElementById("videoVideoAnalysis");const videoRecognition=document.getElementById("videoRecognition");const videoModeling=document.getElementById("videoModeling");const videoPlyerAnalysis=document.getElementById("videoPlyerAnalysis");const play1=document.getElementById("play1");const play2=document.getElementById("play2");const play3=document.getElementById("play3");const play4=document.getElementById("play4");const play5=document.getElementById("play5");function videoConrol(e,t){e.addEventListener("pause",()=>{t.classList.remove("active","hiden");t.classList.add("active");e.removeAttribute("controls")});e.addEventListener("play",()=>{t.classList.remove("active","hiden");t.classList.add("hiden")});t.addEventListener("click",()=>{e.play();t.classList.remove("active","hiden");t.classList.add("hiden");e.setAttribute("controls","controls")})}videoConrol(videoAi,play1);videoConrol(videoAnalysis,play2);videoConrol(videoRecognition,play3);videoConrol(videoModeling,play4);videoConrol(videoPlyerAnalysis,play5);const scrollOpt={block:"center",behavior:"smooth"};const headerLink1=document.getElementById("headerLink1");const headerLink2=document.getElementById("headerLink2");const headerLink3=document.getElementById("headerLink3");const headerLink4=document.getElementById("headerLink4");const headerLink5=document.getElementById("headerLink5");const section1=document.getElementById("section_ai");const section2=document.getElementById("section_video-analysis");const section3=document.getElementById("section_recognition");const section4=document.getElementById("section_modeling");const section5=document.getElementById("section_player-analysis");headerLink1.addEventListener("click",()=>section1.scrollIntoView(scrollOpt));headerLink2.addEventListener("click",()=>section2.scrollIntoView(scrollOpt));headerLink3.addEventListener("click",()=>section3.scrollIntoView(scrollOpt));headerLink4.addEventListener("click",()=>section4.scrollIntoView(scrollOpt));headerLink5.addEventListener("click",()=>section5.scrollIntoView(scrollOpt));let up=null;function doScroll(e){const t=document.getElementById("arrow");const n=document.getElementById("arrowImg");const o=document.getElementById("scroll");const i=document.getElementById("header");const l=window.scrollY+section1.getBoundingClientRect().top;const c=window.scrollY+section2.getBoundingClientRect().top;const s=window.scrollY+section3.getBoundingClientRect().top;const d=window.scrollY+section4.getBoundingClientRect().top;const r=window.scrollY+section5.getBoundingClientRect().top;const a=Math.max(document.body.scrollHeight,document.documentElement.scrollHeight,document.body.offsetHeight,document.documentElement.offsetHeight,document.body.clientHeight,document.documentElement.clientHeight);const m=Math.floor(a*.12);if(e>r-m){n.classList.add("arrow_up");o.innerHTML="up";up=false;t.addEventListener("click",()=>i.scrollIntoView(scrollOpt))}else if(e>=d-m&&up){t.addEventListener("click",()=>section5.scrollIntoView(scrollOpt))}else if(e>=s-m&&up){t.addEventListener("click",()=>section4.scrollIntoView(scrollOpt))}else if(e>=c-m&&up){t.addEventListener("click",()=>section3.scrollIntoView(scrollOpt))}else if(e>=l-m&&up){t.addEventListener("click",()=>section2.scrollIntoView(scrollOpt))}else if(e<=l){t.addEventListener("click",()=>section1.scrollIntoView(scrollOpt));n.classList.remove("arrow_up");o.innerHTML="scroll";up=true}}let lastScrollPosition=0;let ticking=false;window.addEventListener("scroll",()=>{lastScrollPosition=window.scrollY;if(!ticking){window.requestAnimationFrame(()=>{doScroll(lastScrollPosition);ticking=false});ticking=true}});